function afficherEtoile($note)
{
    // Affichage des étoiles en fonction de la note
    // La note est une etoile remplie si elle est supérieure ou égale à 0.8
    $etoiles = '';
    for ($i = 0; $i < 5; $i++) {
        if ($note >= $i + 0.8) {
            $etoiles .= '<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="#FFCA43" class="bi bi-star-fill" viewBox="0 0 16 16">
                            <path d="M3.612 15.443c-.386.198-.824-.149-.746-.592l.83-4.73L.173 6.765c-.329-.314-.158-.888.283-.95l4.898-.696L7.538.792c.197-.39.73-.39.927 0l2.184 4.327 4.898.696c.441.062.612.636.282.95l-3.522 3.356.83 4.73c.078.443-.36.79-.746.592L8 13.187l-4.389 2.256z"/>
                        </svg>';
        } elseif ($note > $i && $note < $i + 1) {
            $etoiles .= '<svg width="16" height="16" viewBox="0 0 30 30" fill="none" xmlns="http://www.w3.org/2000/svg">
